mapper.select:
SELECT PRODUCTIMAGENUM, PRODUCTIMAGEFILENAME, PRODUCTNUM
FROM productImage
WHERE PRODUCTNUM = #{productNum}

mapper.insert:

    SELECT PRODUCT_SEQ.NEXTVAL FROM DUAL

INSERT INTO product (
    PRODUCTNUM, PRODUCTNAME, PRODUCTDESC,
    DELIVERYFEE, MAINIMAGEFILENAME, PRODUCTCLASSIFICATION
) VALUES (
    #{productNum}, #{productName}, #{productDesc},
    #{deliveryFee}, #{mainImageFileName}, #{productClassification}
)

INSERT INTO productDetail (
    PRODUCTNUM, STOCKQUANTITY, UNIT,
    UNITPRICE, DISCOUNTRATE
) VALUES (
    #{productNum}, #{stockQuantity}, #{unit},
    #{unitPrice}, #{discountRate}
)

INSERT INTO rescuedProductInfo (
    PRODUCTNUM, ENDDATE, SUPPLYNUM
) VALUES (
    #{productNum}, #{endDate}, #{supplyNum}
)

mapper.update:
UPDATE product
PRODUCTNAME = #{productName}, PRODUCTDESC = #{productDesc}, DELIVERYFEE = #{deliveryFee}, MAINIMAGEFILENAME = #{mainImageFileName},
WHERE PRODUCTNUM = #{productNum}

UPDATE productDetail
STOCKQUANTITY = #{stockQuantity}, UNIT = #{unit}, UNITPRICE = #{unitPrice}, DISCOUNTRATE = #{discountRate},
WHERE PRODUCTNUM = #{productNum}

UPDATE rescuedProductInfo
ENDDATE = #{endDate}, SUPPLYNUM = #{supplyNum},
WHERE PRODUCTNUM = #{productNum}
