mapper.select:

WHERE block = 0 AND q.memberId = #{memberId}
ORDER BY qnaDate DESC
OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT COUNT(*)
FROM productQna q


        AND q.productNum = #{productNum}


        AND q.block = #{block}




                AND q.answerId IS NOT NULL


                AND q.answerId IS NULL
